import { createSlice } from "@reduxjs/toolkit"

export const newsSlice = createSlice({
    name: 'news',
    initialState: {
        value: []
    },
    reducers: {
        set: (state, action) => {
            state.value = state.value.concat(action.payload)
        }
    }
})

export const {set} = newsSlice.actions

export default newsSlice.reducer
